import React, { useState } from "react";

export default function App() {
  const [query, setQuery] = useState("");
  const [results, setResults] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  async function doSearch(e) {
    e.preventDefault();
    setError(null);

    if (!query.trim()) {
      setError("Type a course name to search");
      return;
    }

    setLoading(true);
    try {
      const res = await fetch(`/recommend?q=${encodeURIComponent(query)}&top_n=4`);
      if (!res.ok) throw new Error("Server error");

      const data = await res.json();
      setResults(data.results || []);
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  }

  return (
    <div className="min-h-screen flex items-start justify-center p-6 bg-gray-50">
      <div className="w-full max-w-2xl bg-white p-6 rounded-2xl shadow">
        <h1 className="text-2xl font-bold mb-4">
          Course Search (TF-IDF + Cosine Similarity)
        </h1>

        <form onSubmit={doSearch} className="flex gap-2 mb-4">
          <input
            className="flex-1 p-3 border rounded"
            value={query}
            onChange={(e) => setQuery(e.target.value)}
            placeholder="Type course name..."
          />
          <button className="p-3 bg-blue-600 text-white rounded" type="submit">
            Search
          </button>
        </form>

        {loading && <div>Loading…</div>}
        {error && <div className="text-red-600">{error}</div>}

        <div className="grid gap-3 mt-4">
          {results.map((r, i) => (
            <a
              key={i}
              href={r.url || "#"}
              target="_blank"
              rel="noreferrer"
              className="p-3 border rounded hover:shadow"
            >
              <div className="font-semibold">{r.title}</div>
              <div className="text-sm text-gray-600">
                Rating: {r.rating ?? "N/A"} — Score: {r.score.toFixed(3)}
              </div>
              {r.url && (
                <div className="text-xs text-blue-600 break-all">{r.url}</div>
              )}
            </a>
          ))}
        </div>
      </div>
    </div>
  );
}
